/*! Simplified EXIF.js for offline use */
var EXIF = {
  getData: function(img, callback) {
    var reader = new FileReader();
    reader.onload = function(e) {
      img.exifdata = EXIF.readFromBinaryFile(e.target.result);
      if (callback) callback.call(img);
    };
    reader.readAsArrayBuffer(img);
  },
  getTag: function(img, tag) {
    return img.exifdata ? img.exifdata[tag] : null;
  },
  readFromBinaryFile: function() {
    // Données simulées pour test hors ligne
    return {
      GPSLatitude: [43, 12, 30],
      GPSLongitude: [5, 56, 15],
      GPSLatitudeRef: "N",
      GPSLongitudeRef: "E"
    };
  }
};
